class Solution:
    """
    @param houses: positions of houses
    @param heaters: positions of heaters
    @return: the minimum radius standard of heaters
    """
    def find_radius(self, houses, heaters):
        # Write your code here
        def cover(houses, heaters, radius):
            idx = 0
            idx2 = 0
            while idx < len(houses) and idx2 < len(heaters):
                if heaters[idx2] - radius <= houses[idx] <= heaters[idx2] + radius:
                    idx += 1
                else:
                    idx2 += 1

            if idx != len(houses):
                return False
            else:
                return True

        houses = sorted(houses)
        heaters = sorted(heaters)

        left = 0
        right = 10 ** 9
        while left + 1 < right:
            middle = (left + right) // 2
            if cover(houses, heaters, middle):
                right = middle
            else:
                left = middle + 1

        if cover(houses, heaters, left):
            return left
        else:
            return right


